import java.io.FileNotFoundException;import java.io.FileReader;import java.io.IOException;import java.io.PrintWriter;import java.util.Scanner;import javax.swing.JOptionPane;public class ms1cc{public static void main(String[]args)throws FileNotFoundException,IOException{String sn,fn,mi,ln,pc;int sc=0;FileReader s=new FileReader("C:\\studentRecord.txt");PrintWriter o=new PrintWriter("output.txt");Scanner f=new Scanner(s);String ic=JOptionPane.showInputDialog(null,"Enter valid program code: ","Input",JOptionPane.QUESTION_MESSAGE);while(f.hasNext()){sn=f.next();fn=f.next();mi=f.next();ln=f.next();pc=f.next();if(ic.equals(pc)){System.out.println(fn+"\t"+mi+"\t"+ln);sc++;}}if(sc>0){o.write(ic+"\t"+sc);o.close();}else System.out.println("No matches found.");s.close();f.close();}}